mapper.update:

   UPDATE product 
   SET name=#{name},term=#{term},price=#{price}, explan=#{explan},amount=#{amount}

	product_id =#{product_id}
	and			
	delete_check =1

mapper.insert:
INSERT INTO payment
(payment_id, product_id, enterprise_id, date) VALUES
(null, #{product_id}, #{enterprise_id}, null);

mapper.select:
select  product.amount  from product, payment

	payment.product_id = product.product_id
	and payment.payment_id = #{payment_id}

select  product.type  from product, payment

	payment.product_id = product.product_id
	and payment.payment_id = #{payment_id}

mapper.update:
update enterprise
SET up_count = up_count + #{product_count}

	enterprise_id =#{enterprise_id}
